// Harga
const hargaA = 3000;  // Es Teh
const hargaB = 10000; // Mie Ayam
const hargaC = 1000;  // Bakwan

// Jumlah
let qtyA = 0;
let qtyB = 0;
let qtyC = 0;

// Elemen
const qtyAEl = document.getElementById("qtyA");
const qtyBEl = document.getElementById("qtyB");
const qtyCEl = document.getElementById("qtyC");
const totalEl = document.getElementById("totalText");
const uangEl = document.getElementById("uang");
const kembaliEl = document.getElementById("kembalianText");

// Format sangat sederhana
function rupiah(n) {
  return "Rp " + n;
}

function update() {
  qtyAEl.textContent = qtyA;
  qtyBEl.textContent = qtyB;
  qtyCEl.textContent = qtyC;

  const total = (qtyA * hargaA) + (qtyB * hargaB) + (qtyC * hargaC);
  totalEl.textContent = rupiah(total);

  const uang = Number(uangEl.value || 0);
  const kembalian = uang - total;
  kembaliEl.textContent = rupiah(kembalian > 0 ? kembalian : 0);
}

// Klik menu -> tambah qty
document.getElementById("btnA").onclick = function() {
  qtyA = qtyA + 1;
  update();
};

document.getElementById("btnB").onclick = function() {
  qtyB = qtyB + 1;
  update();
};

document.getElementById("btnC").onclick = function() {
  qtyC = qtyC + 1;
  update();
};

// Uang berubah -> update kembalian
uangEl.oninput = update;

// Reset
document.getElementById("reset").onclick = function() {
  qtyA = 0;
  qtyB = 0;
  qtyC = 0;
  uangEl.value = "";
  update();
};

update();
